mapper.update:
update dish
name=#{name}, category_id=#{categoryId}, price=#{price}, image=#{image}, description=#{description}, status=#{status}, create_time=#{createTime}, create_user=#{createUser}, update_time=#{updateTime}, update_user=#{updateUser},
where id=#{id}

mapper.select:
select dish.*,category.name as categoryName from dish,category

    dish.category_id=category.id
    and dish.name like concat('%',#{name},'%')
    and dish.category_id=#{categoryId} 
    and dish.status=#{status}

order by create_time desc
